import React, { Component } from 'react'
class CheckBox extends Component{

    constructor(props) {
        super(props)
        this.content =[]
        this.state = {
        list:[],
        value:"",
        msg:""
        }
      }

      handleCheckBox = (e) =>{

         var val=this.content.indexOf(e.target.value)
         if(val==-1){ 

            this.content = this.content.concat(e.target.value)
         }
         else{
             this.content.splice(val,1)
         }
        this.setState({
            list:this.content
          })
          console.log(this.content)
      }

      handleClick =(e)=>{
        this.setState({

            msg:"you have visited these cities "+this.state.list,
            value:" "
        });

    function createCities(cities){

        var tokens=[]
        for(let i=0;i<this.cities.length;i++){
            tokens[i]=<h1>{this.cities[i]}</h1>
        }
        return tokens 
    }

      }
    render(){
        return(
            <div>
                {this.createCities(this.props.city)}

                 <button onClick={this.handleClick}>SUBMIT</button>

            </div>
        )
    }
}
export default CheckBox;
