import React, { Component } from 'react'
class CheckBox extends Component{

    constructor(props) {
        super(props)
        this.state = {
        value:"",
        msg:""
        }
      }

      handleRadioClick = (e) =>{
          this.setState({
              value:e.target.value
          })
      }
      handleClick =(e)=>{
        console.log(this.state.msg)
        this.setState({
            msg:"you have visited these cities "+this.state.value
        });

      }
    render(){
        return(
            <div>
                <input type="checkbox"
                 value = "Hyderabad"
                 checked={this.state.value ==="Hyderabad"}
                 onChange ={this.handleCheck}
                 ></input><label>Hyderabad</label>
            </div>
        )
    }
}
export default CheckBox;
