import React, { Component } from 'react'

import { thisTypeAnnotation } from '@babel/types';
class CommentBox extends Component{
    constructor(props) {
        super(props)
        this.localArray=[]
        this.state = {
        value:"",
        comments:[],
        msg:""
        }
      }
    handleChange=(e)=>{
        this.setState({
            value:e.target.value,
            msg:" "
        })
    }
    handleComment=()=>{
        this.localArray = this.localArray.concat(this.displayComments(this.state.value))
        this.setState({
            comments:this.localArray
        })
      }
      displayComments=(val)=>{
          return <div className="comment"><h2>Tony stark</h2>{val}</div>
      }
    render(){
        return(
            <div >
                <input type="text" value={this.state.value} onChange={this.handleChange} onFocus={this.handleFocus} />
                <button onClick={this.handleComment} value="comment">Comment</button>
                {this.state.comments}
            </div>
        )
    }
}
export default CommentBox
