import React, { Component } from 'react'

class ClickMe extends Component{
    constructor(props) {
        super(props)
        this.state = {
        value:"",
        msg:""
        }
      }
      handleCheckBox = (e) =>{
        this.setState({
            value:e.target.value
        })
        console.log(this.state.value)
    }
    handleClick = (e) =>{
        let ref = "ref";
        if(this.state.value === "Disabled"){

            window.alert("Iam working")
        }
        this.refs["check"].checked = !this.refs["check"].checked;
    }
    render(){
        return(
            <div>
                <input type="checkbox"
                 value = "Disabled"
                 checked={this.state.value === "Disabled"}
                 onChange ={this.handleCheckBox}
                 ref = "check"
                 ></input><label>Disabled</label><br></br>
                <button onClick={this.handleClick}>Click Me</button>
            </div>
        )
    }
}
export default ClickMe
