import React, { Component } from 'react'
class CheckBox extends Component{

    constructor(props) {
        super(props)
        this.state = {
        list:[],
        value:"",
        msg:""
        }
      }

      handleCheckBox = (e) =>{
          this.setState({
              value:this.state.value+" "+e.target.value
          })
          console.log(e.target.value)
      }
      handleClick =(e)=>{
        this.setState({
            msg:"you have visited these cities "+this.state.value,
            value:" "
        });

      }
    render(){
        return(
            <div>
                <input type="checkbox"
                 value = "Hyderabad"
                 checked={this.state.value ==="Hyderabad"}
                 onChange ={this.handleCheckBox}
                 /><label>Hyderabad</label><br></br>

                  <input type="checkbox"
                 value = "Banglore"
                 checked={this.state.value ==="Banglore"}
                 onChange ={this.handleCheckBox}
                 ></input><label>Banglore</label><br></br>
                  <input type="checkbox"
                 value = "Delhi"
                 checked={this.state.value ==="Delhi"}
                 onChange ={this.handleCheckBox}
                 ></input><label>Delhi</label><br></br>
                  <input type="checkbox"
                 value = "Mumbai"
                 checked={this.state.value ==="Mumbai"}
                 onChange ={this.handleCheckBox}
                 ></input><label>Mumbai</label><br></br>
                 <button onClick={this.handleClick}>SUBMIT</button>
                 <h3>{this.state.msg}</h3>
            </div>
        )
    }
}
export default CheckBox;
