import React, { Component } from 'react'
class CheckBox extends Component{

    constructor(props) {
        super(props)
        this.content =[]
        this.state = {
        list:[],
        value:"",
        msg:""
        }
      }

      handleCheckBox = (e) =>{
        var val=this.content.indexOf(e.target.value)
         if(val==-1){ 

            this.content = this.content.concat(e.target.value)
         }
         else{
             this.content.splice(val,1)
         }
        this.setState({
            list:this.content
          })
          console.log(this.content)
      }

      handleClick =(e)=>{
        this.setState({

            msg:"you have visited these cities "+this.state.list,
            value:" "
        });

      }
    render(){
        return(
            <div>
                <input type="checkbox"
                 value = "Hyderabad"
                 onChange ={this.handleCheckBox}
                 /><label>Hyderabad</label><br></br>

                 <input type="checkbox"
                 value = "Banglore"                
                 onChange ={this.handleCheckBox}
                 ></input><label>Banglore</label><br></br>

                <input type="checkbox"
                 value = "Delhi"
                 onChange ={this.handleCheckBox}
                 ></input><label>Delhi</label><br></br>

                <input type="checkbox"
                 value = "Mumbai"
                 onChange ={this.handleCheckBox}
                 ></input><label>Mumbai</label><br></br>
                 <button onClick={this.handleClick}>SUBMIT</button>
                 <h3>{this.state.msg}</h3>
            </div>
        )
    }
}
export default CheckBox;
