import React, { Component } from 'react'

import MsgBody from './MsgBody'

class GreetButton extends Component{


    constructor(props) {
        super(props)
        this.state = {
         value:""
        }
      }

    onhandleClick = () => {
        this.setState({
            value: this.state.value
        })
      }

    render(){
        return(
            <div>
                <input type = "text" value={this.props.value} ></input>

                <br></br>
                <button onClick={this.onhandleClick}>GREET</button>
                <MsgBody name={this.state.value}/>
            </div>
        )
    }
}
export default GreetButton;
