import React, { Component } from 'react'

class DropDown extends Component{

    constructor(props) {
        super(props)
        this.state = {
        value:"",
        msg:""
        }
      }
      change=(e)=>{
          this.setState({
            value:e.target.value
          });
      }
      handleDrop=(e)=>{
          this.setState({
                msg:"you are from "+this.state.value
          });
      }


      render(){
        return(
            <div>
              <select>
              <option value="Andhra" onChange={this.change}>Andhra</option>
               <option value="Telangana" onChange={this.change}>Telangana</option>
               <option value="Kerala" onChange={this.change}>Kerala</option>
               <option value="TamilNadu" onChange={this.change}>TamilNadu</option>
             </select>
             <button onClcik={this.handleDrop}>SUBMIT</button>
             <h3>{this.state.msg}</h3>
            </div>
        )
    };
}
export default DropDown
