import React, { Component } from 'react'
class CheckBox extends Component{

    constructor(props) {
        super(props)
        this.content =[]
        this.state = {
        list:[],
        value:"",
        msg:""
        }
      }

      handleCheckBox = (e) =>{
            var val=e.target.value
         if(this.content.findIndex(e.target.value)==-1){ 

            this.content = this.content.concat(e.target.value)
         }
        this.setState({
            list:this.state.list.concat(e.target.value)
          })
          console.log(this.content)
      }

      handleClick =(e)=>{
        this.setState({
            msg:"you have visited these cities "+this.state.value,
            value:" "
        });

      }
    render(){
        return(
            <div>
                <input type="checkbox"
                 value = "Hyderabad"
                 onChange ={this.handleCheckBox}
                 /><label>Hyderabad</label><br></br>

                 <input type="checkbox"
                 value = "Banglore"                
                 onChange ={this.handleCheckBox}
                 ></input><label>Banglore</label><br></br>

                <input type="checkbox"
                 value = "Delhi"
                 onChange ={this.handleCheckBox}
                 ></input><label>Delhi</label><br></br>

                <input type="checkbox"
                 value = "Mumbai"
                 onChange ={this.handleCheckBox}
                 ></input><label>Mumbai</label><br></br>
                 <button onClick={this.handleClick}>SUBMIT</button>
                 <h3>{this.state.msg}</h3>
            </div>
        )
    }
}
export default CheckBox;
