import React, { Component } from 'react'

class RadioButtons extends Component{
    constructor(props) {
        super(props)
        this.state = {
        value:""
        }
      }
      handleRadioClick =(e)=>{
        console.log(this.state.value)
        this.setState({
            value:"your favourite is"+this.state.value
        });

      }
    render(){
        return(
            <div>

              <h3>What is your favourite Ice cream</h3>
              <input type="radio" 
                    name="IceCream" 
                    value="Vanilla" 
                    onChange={this.handleRadioClick} 
                    checked={this.state.value === "Vanilla"}/>Vanilla
                    <br></br>
              <input type="radio"
                     name="IceCream"
                     value="Butterscotch"
                     onChange={this.handleRadioClick}
                      checked={this.state.value ===" Butterscotch"}/>Butterscotch
                      <br></br>
              <input type="radio"
                    name="IceCream"
                     value="Chocolate"
                    onChange={this.handleRadioClick}
                    checked={this.state.value==="Vanilla"}/>Chocolate
                    <br></br>
              <input type="radio"
                    name="IceCream"
                     value="Strawberry"
                      onChange={this.handleRadioClick}
                       checked={this.state.value==="Vanilla"}/>Strawberry
                       <br></br>
              <button></button>
              <h3></h3>
            </div>
        )
    }
}
export default RadioButtons;
