import React, { Component } from 'react'

import "./style.css"
class StopWatch extends Component{
    constructor(props) {
        super(props)
        this.state = {
        timerSec : 0,
        seconds:6000,
        timerMin : 0
        }
      }

      startTimer=(e)=>{

        setTimeout(this.changeSeconds,1000);
        if(this.state.TimerSec == 6){
            this.setState({
                timerMin:this.state.timerMin+1,
                timerSec:this.state.timerSec*0
            })
        }

      }
    changeSeconds=()=>{
        this.setState({
            timerSec:this.state.timerSec+1
        })
        this.startTimer()
      }

    render(){
        return(
            <div className = "watch">
                <h1 >{Math.floor(this.state.timerSec/60)+":"+this.state.timerSec}</h1><br></br>
                <button onClick={this.startTimer} value="start">START</button>
            </div>
        )
    }
}
export default StopWatch
