import React, { Component } from 'react'

import "./style.css"
class StopWatch extends Component{
    constructor(props) {
        super(props)
        this.state = {
        timerSec : 0,
        seconds:6000,
        timerMin : 0
        }
      }

      startTimer=(e)=>{
            if(e.target.value=="stop"){
                return ""
            }
            setTimeout(this.changeSeconds,1000);

      }
    changeSeconds=()=>{
        this.setState({
            timerSec:this.state.timerSec+1
        })
        if(this.state.timerSec>=60){
            this.setMin()
            this.setState({
                timerSec:this.state.timerSec*0
            }) 

        }
        console.log()
        this.startTimer()
     }
     setMin=()=>{
        var mins;
        this.setState({
            timerMin:this.state.timerMin+1
        })

     }
      stopTimer=()=>{
        this.setState({
            timerSec:this.state.timerSec

        })
      }
    render(){
        return(
            <div className = "watch">
                <h1 >{this.state.timerMin+":"+this.state.timerSec}</h1><br></br>
                <button onClick={this.startTimer} value="start">START</button>
                <button onClick={this.startTimer} value="stop">STOP</button>
            </div>
        )
    }
}
export default StopWatch
