import React, { Component } from 'react'

import "./style.css"
class StopWatch extends Component{
    constructor(props) {
        super(props)
        this.state = {
        timerSec : 0,
        seconds:60000,
        timerMin : 0
        }
      }

      startTimer=(e)=>{

        setTimeout(this.changeSeconds(60000),60000);

      }
    changeSeconds=(val)=>{
        this.setState({
            seconds:val-this.state.seconds,
            timerSec:this.state.seconds,
            seconds:this.state.seconds-1

        })
      }

    render(){
        return(
            <div className = "watch">
                <h1 >{this.state.timerMin+":"+this.state.timerSec}</h1><br></br>
                <button onClick={this.startTimer} value="start">START</button>
            </div>
        )
    }
}
export default StopWatch
